package substring;

public class substr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="helloll";
		String reverse=str;
		int no=5;
		for(int i=0;i<no;i++)
		{
		System.out.println(reverse.substring(reverse.length()-no));
		}
		if(reverse.str(equals))
		{
			System.out.println("given string is palindrome");
		}
		else {
			System.out.println("given string is not palindrome");
		}

	}

}
